<?phprequire_once('dbconfig.php');class carros{	private $conn;	public function __construct(){		$database = new Database();		$db = $database->dbConnection();		$this->conn = $db;    }	public function runQuery($sql){		$stmt = $this->conn->prepare($sql);		return $stmt;	}		public function register($valor){		try{		    $nome = $valor["nome"];            $carroid = $valor["carroid"];            $marca = $valor["marca"];            $preco = $valor["preco"];            if($nome=="")	{                return "preencha o nome!";            }            else {                if ($carroid <= 0) {                    $stmt = $this->conn->prepare("INSERT INTO carros(nome,marca,preco) VALUES(:nome,:marca,:preco)");                    $stmt->bindparam(":nome", $nome);                    $stmt->bindparam(":marca", $marca);                    $stmt->bindparam(":preco", $preco);                } else {                    $stmt = $this->conn->prepare("UPDATE carros SET nome = :nome, marca = :marca, preco = :preco WHERE carroid = :carroid");                    $stmt->bindparam(":nome", $nome);                    $stmt->bindparam(":marca", $marca);                    $stmt->bindparam(":preco", $preco);                    $stmt->bindparam(":carroid", $carroid);                }                $stmt->execute();                return "0";            }		}		catch(PDOException $e){			echo $e->getMessage();		}	}    public function deletar($uid){        try{            $stmt = $this->conn->prepare("UPDATE carros SET excluido = 'S' WHERE carroid = :uid");            $stmt->bindparam(":uid", $uid);            $stmt->execute();            return $stmt;        }        catch(PDOException $e) {            echo $e->getMessage();        }    }	public function redirect($url){		header("Location: $url");	}}?>