<?phprequire_once('dbconfig.php');class cores{	private $conn;	public function __construct(){		$database = new Database();		$db = $database->dbConnection();		$this->conn = $db;    }	public function runQuery($sql){		$stmt = $this->conn->prepare($sql);		return $stmt;	}	public function register($valor){		try{		    $nome = $valor["nome"];            $variacao = $valor["variacao"];            $coreid= $valor["coreid"];            if($nome=="")	{                return "preencha o nome!";            }            else {                if ($coreid <= 0) {                    $stmt = $this->conn->prepare("INSERT INTO cores(nome,variacao) VALUES(:nome,:variacao)");                    $stmt->bindparam(":nome", $nome);                    $stmt->bindparam(":variacao", $variacao);                } else {                    $stmt = $this->conn->prepare("UPDATE cores SET nome = :nome, variacao = :variacao WHERE coreid = :coreid");                    $stmt->bindparam(":nome", $nome);                    $stmt->bindparam(":variacao", $variacao);                    $stmt->bindparam(":coreid", $coreid);                }                $stmt->execute();                return "0";            }		}		catch(PDOException $e){			echo $e->getMessage();		}	}    public function deletar($uid){        try{            $stmt = $this->conn->prepare("UPDATE cores SET excluido = 'S' WHERE coreid = :uid");            $stmt->bindparam(":uid", $uid);            $stmt->execute();            return $stmt;        }        catch(PDOException $e) {            echo $e->getMessage();        }    }	public function redirect($url){		header("Location: $url");	}}?>