<?phprequire_once('dbconfig.php');class marcas{	private $conn;	public function __construct(){		$database = new Database();		$db = $database->dbConnection();		$this->conn = $db;    }	public function runQuery($sql){		$stmt = $this->conn->prepare($sql);		return $stmt;	}		public function register($valor){		try{		    $nome = $valor["nome"];            $marcaid= $valor["marcaid"];            if($nome=="")	{                return "preencha o nome!";            }            else {                if ($marcaid <= 0) {                    $stmt = $this->conn->prepare("INSERT INTO marcas(nome) VALUES(:nome)");                    $stmt->bindparam(":nome", $nome);                } else {                    $stmt = $this->conn->prepare("UPDATE marcas SET nome = :nome WHERE marcaid = :marcaid");                    $stmt->bindparam(":nome", $nome);                    $stmt->bindparam(":marcaid", $marcaid);                }                $stmt->execute();                return "0";            }		}		catch(PDOException $e){			echo $e->getMessage();		}	}    public function deletar($uid){        try{            $stmt = $this->conn->prepare("UPDATE marcas SET excluido = 'S' WHERE marcaid = :uid");            $stmt->bindparam(":uid", $uid);            $stmt->execute();            return $stmt;        }        catch(PDOException $e) {            echo $e->getMessage();        }    }	public function redirect($url){		header("Location: $url");	}}?>